package com.fourteen.springboottest;

import cn.hutool.core.io.FileUtil;
import net.coobird.thumbnailator.Thumbnails;
import org.apache.pdfbox.pdmodel.PDDocument;
import org.apache.pdfbox.pdmodel.common.PDRectangle;
import org.junit.jupiter.api.Test;
import org.springframework.boot.test.context.SpringBootTest;

import java.io.ByteArrayInputStream;
import java.io.ByteArrayOutputStream;
import java.io.IOException;

/**
 * @author Fourteen_ksz
 * @version 1.0
 * @date 2024/5/11 16:13
 */
@SpringBootTest
public class GetPdfSize {

    @Test
    void getPdfSize() {
        String base64 = "";

        String path = "D:\\Downloads\\资讯报告导出-20240313.pdf";
        byte[] bytes = FileUtil.readBytes(path);

        float blockH = 0.0404478f;
        float blockW = 0.138776f;
        try (PDDocument document = PDDocument.load(bytes)) {
            PDRectangle mediaBox = document.getPage(0).getMediaBox();
            float pdfH = mediaBox.getHeight();
            float pdfW = mediaBox.getWidth();

            byte[] imageBytes = java.util.Base64.getDecoder().decode(base64);
            ByteArrayInputStream bis = new ByteArrayInputStream(imageBytes);

            ByteArrayOutputStream bos = new ByteArrayOutputStream();

            Thumbnails.of(bis)
                    .size((int) (pdfW * blockW), (int) (pdfH * blockH))
                    .keepAspectRatio(false)
                    .outputFormat("png")
                    .toOutputStream(bos);

            byte[] resizedImageBytes = bos.toByteArray();
            bis.close();
            bos.close();
            System.out.println(java.util.Base64.getEncoder().encodeToString(resizedImageBytes));
        } catch (IOException e) {
            throw new RuntimeException(e);
        }
    }

}
